mapper.select:
SELECT *
FROM users
WHERE provider = #{provider}
  AND oauth_id = #{oauthId}
  AND deleted = 0

mapper.insert:
INSERT INTO users (
   nickname, introduction, profile_img_url, taste_score, manner_score,
   leader_count, crew_count, reported_count, enabled, provider, oauth_id,
   created_at, updated_at, deleted
) VALUES (
    #{nickname}, #{introduction}, #{profileImgUrl}, #{tasteScore}, #{mannerScore},
    #{leaderCount}, #{crewCount}, #{reportedCount}, #{enabled}, #{provider}, #{oauthId},
    now(), now(), #{deleted}
 )

mapper.update:
UPDATE users
SET nickname = #{nickName},
    profile_img_url = #{profileImgUrl},
    introduction = #{introduction}
WHERE id = #{userId}
  AND deleted = 0

UPDATE users
SET deleted = 1
WHERE id = #{userId}
